import Header from "./components/Header";
import Drawer from "./components/Drawer";
import Card from "./components/Card/Card";

const arr = [
    {
        title: 'Мужские Кроссовки Nike Blazer Mid Suede',
        price: '12 999 ₽',
        imgUrl: "/img/kross7.jpg"
    },
    {
        title: 'Мужские Кроссовки Nike AIR MAX 2700',
        price: '9 906 ₽',
        imgUrl: "/img/kross6.jpg"
    },
    {
        title: 'Мужские Кроссовки Nike AIR MAX 2700',
        price: '3 698 ₽',
        imgUrl: "/img/kross5.jpg"
    },
    {
        title: 'Мужские Кроссовки Nike AIR MAX 2700',
        price: '5 878 ₽',
        imgUrl: "/img/kross4.jpg"
    },
    {
        title: 'Мужские Кроссовки Nike AIR MAX 2700',
        price: '6 258 ₽',
        imgUrl: "/img/kross3.jpg"
    },
    {
        title: 'Я lovv66',
        price: '10 066 ₽',
        imgUrl: "/img/kross2.jpg"
    },

]

function App() {
    let counter =7
    const plus = () => {
        counter++
    }
    const minus = () => {
        counter--
    }

    return <div className="wrapper clear">
        <center>
            <h1>{counter}</h1>
            <button onClick={ plus }>+</button>
            <button onClick={ minus }>-</button>
        </center>



        <Drawer/>
        <Header/>

        <div className="content p-40">

            <div className=" d-flex align-center justify-between mb-10">

                <h1 className="">Хиты продаж</h1>
            </div>

            <div className="products d-flex">

                {arr.map((obj) => (
                    <Card title={obj.title}
                          price={obj.price}
                          imgUrl={obj.imgUrl}
                          onFavorite={() => console.log('Добавили в Избранное')}
                          onPlus={() => console.log('Добавили в Корзину')}
                    />
                ))}

            </div>
        </div>
    </div>
}


export default App;
