import React from "react"
import Card from "../Card/Card";



function Dix( items,
            searchValue,
            onAddToFavorite,
            onAddToCard ) {
    return <div>
        <div>
                <div className="content p-40">
                    <div className=" d-flex align-center justify-between mb-10">
                        <h1 className="">{props.searchValue ? `Результат по запросу "${props.searchValue}"` : 'Хиты продаж'}</h1>
                    </div>
                    <div className="d-flex flex-wrap">

                        {props.items
                            // .filter((item) => item.title.toLowerCase().includes(searchValue.toLocaleLowerCase()))
                            .map((item, index) => (
                                <Card
                                    key={index}
                                    title={item.title}
                                    price={item.price}
                                    imgUrl={item.imgUrl}
                                    onFavorite={(obj) => onAddToFavorite(obj)}
                                    onPlus={(obj) => props.onAddToCard(obj)}
                                />
                            ))}
                    </div> 
                </div>
            </div>
    </div>

}


export default Dix
