import React from "react"
import Header from "./components/Header";
import Drawer from "./components/Drawer";
import Card from "./components/Card/Card";


function App() {
    const  [items, setItems] = React.useState([])
    const [cardOpened, setCardOpened] = React.useState(false)

    fetch ('https://61a77816387ab200171d2cf9.mockapi.io/:items')

    return <div className="wrapper clear">


        {cardOpened && <Drawer onClose={() => setCardOpened(false)} />}
        <Header onCliclCard={() => setCardOpened(true)}/>


        <div className="content p-40">

            <div className=" d-flex align-center justify-between mb-10">

                <h1 className="">Хиты продаж</h1>
            </div>

            <div className="products d-flex flex-wrap">

                {items.map((obj) => (
                    <Card title={obj.title}
                          price={obj.price}
                          imgUrl={obj.imgUrl}
                          onFavorite={() => console.log('Добавили в Избранное')}
                          onPlus={() => console.log('Добавили в Корзину')}
                    />
                ))}

            </div>
        </div>
    </div>
}


export default App;
