import React from "react"
import Header from "./components/Header";
import Drawer from "./components/Drawer";
import Card from "./components/Card/Card";


function App() {
    const  [items, setItems] = React.useState([])
    const  [cardItems, setCardItems] = React.useState([])
    const  [searchValue, setSearchValue] = React.useState('')
    const [cardOpened, setCardOpened] = React.useState(false)

    React.useEffect(() => {
        fetch ('https://61a77816387ab200171d2cf9.mockapi.io/items').then((res) => {
            return res.json()
        }).then(json => {
            setItems(json)
        })
    }, [])

    const onAddToCard = (obj) => {
        setCardItems(prev => [...prev, obj])

    }

    const onChangeInput = (event) => {
        setSearchValue(event.target.value)
    }
    const filterChange = items.filter(item => {
        return item.title.toLowerCase().includes(searchValue.toLowerCase())
    })


    return <div className="wrapper clear">


        {cardOpened && <Drawer items={cardItems} onClose={() => setCardOpened(false)} />}

        <Header onClickInput={onChangeInput} value={searchValue} setValue={setSearchValue} onCliclCard={() => setCardOpened(true)}/>


        <div className="content p-40">

            <div className=" d-flex align-center justify-between mb-10">

                <h1 className="">{searchValue ? `Результат по запросу "${searchValue}"` : 'Хиты продаж'}</h1>

                <div className="search_block d-flex">
                <img src="/img/search.svg" alt="search" />

               {searchValue && <img onClick={() => setSearchValue('')} className="rem cu-p" src="/img/btn-remove.svg" alt="Close"/>}

                <input 
                type="text"
                onChange={(event) => setSearchValue(event.target.value)} 
                value={searchValue} 
                placeholder="Я ищу..." />

            </div>
            </div>

            <div className="d-flex flex-wrap">

                {items
                    .filterChange.map((item, i) => (
                    <Card
                          title={item.title}
                          price={item.price}
                          imgUrl={item.imgUrl}
                          onFavorite={() => console.log('Добавили в Избранное')}
                          onPlus={(obj) => onAddToCard(obj)}
                    />
                ))}
            </div>
        </div>
    </div>
}


export default App;
